import React from "react";
import styled from "styled-components";
import rain from "../img/rain.jpg";
import cloud from "../img/clouds.jpg";
import sunny from "../img/sunny.jpg";

function Description(data) {
  const cloud = "cloud";
  const rain = "rain";
  const clearSky = "clear";
  const indexOfFirstCloud = data.weather.indexOf(cloud);
  const indexOfFirstRain = data.weather.indexOf(rain);
  const indesofFirstClearSky = data.weather.indexOf(clearSky);
  return (
    <DescriptionWrapper>
      <div>
        {indexOfFirstCloud != -1 && (
          <>
            <CloudBg /> <CloudSvg />
          </>
        )}
        {indexOfFirstRain != -1 && (
          <>
            <Rainsvg /> <RainBg />{" "}
          </>
        )}
        {indesofFirstClearSky != -1 && (
          <>
            <ClearskySvg /> <SunnyBg />{" "}
          </>
        )}
      </div>
      {Description && <h1 className="weather">{data.weather}</h1>}
    </DescriptionWrapper>
  );
}

export default Description;

const BgImg = styled.img`
  position: absolute;
  top: 0;
  left: 0;
  z-index: -1;
`;

const SunnyBg = () => {
  return (
    <div>
      <BgImg src={sunny} />
    </div>
  );
};
const RainBg = () => {
  return (
    <div>
      <BgImg src={rain} />
    </div>
  );
};
const CloudBg = () => {
  return (
    <div>
      <BgImg src={cloud} />
    </div>
  );
};
const DescriptionWrapper = styled.div`
  display: flex;
  justify-content: space-around;
  align-items: center;
  max-width: 4rem;
  margin: 0 2rem;

  h1 {
    margin-left: 1rem;
  }
`;
